import React, {Component, useContext} from 'react'
import {MdLocalShipping} from 'react-icons/md';
import {GrCreditCard} from 'react-icons/gr';
import CheckoutElement from './CheckoutElement';
import Title from '../Title';
import {ProductContext} from '../../contextAPI';
import Error from '../../pages/Error';
import Loading from '../Cart/LoadingCart';
import {Link} from 'react-router-dom';

export default class Checkout extends Component {

    constructor(){
        super();
        this.state = {
            name: "",
            nameError: "",
            surname: "",
            surnameError: "",
            address: "",
            addressError:"",
            number: "",
            numberError: "",
            city: "",
            cityError: "",
            state: "",
            stateError: "",
            cap: "",
            capError: "",
            email: "",
            emailError: "",
            country: "",
            countryError: "",
            card: "",
            cardError: "",
            month: "",
            year: "",
            expireError: "",
            cvc: "",
            cvcError: "",
            toBuy: false
        }
    }

    static contextType = ProductContext;

    componentDidMount(){
        window.scrollTo(0,0);
    }

    handleChanges = event => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({
            [name] : value
        })
        checkErrors();
    }

    checkErrors = () => {

    }

    buy = () => {
        let {
            toBuy,
            name,
            surname,
            email,
            address,
            number,
            city,
            state,
            country,
            cap,
            month,
            year,
            cvc,
            card
        } = this.state;

        let {
            errName,
            errSurname,
            errEmail,
            errAdd,
            errNum,
            errCity,
            errState,
            errCountry,
            errCap,
            errCVC,
            errExpire,
            errCard
        } = false;

        //const context=useContext(ProductContext);
        const mailFormat = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
        const {buyItems} = this.context;

        if(toBuy){

            if(!errName && !errSurname && !errEmail && !errAdd && !errNum && !errState && !errCountry && !errCity && !errCap && !errCVC && !errCard && !errExpire)
                buyItems();
        }
    }

    render() {
        const {cart,cartLoading,cartTotal} = this.context;
        const month = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul","Aug", "Sep", "Oct", "Nov", "Dec"];
        var year = [];
        for(var i=2020;i<=2030;i++)
            year.push(i);
        if(cartLoading)
            return <Loading></Loading>
        else if(cart.length === 0)
            return <Error></Error>
        else 
            return (
            <>
            <Title title="checkout"></Title>
            <div className="checkout-container">
                <div className="form-wrapper">
                    <div className="form-container">
                        <form action="">
                            <h1>
                                <MdLocalShipping className="checkout-icon"></MdLocalShipping> Shipping Details
                            </h1>
                            <div className="name">
                                <div>
                                    <label htmlFor="name">name</label>
                                    <input type="text" name="name" onChange={this.handleChanges}/>
                                    <div className="errorForm">
                                        {this.state.nameError}
                                    </div>
                                </div>
                                <div>
                                    <label htmlFor="surname">surname</label>
                                    <input type="text" name="surname" onChange={this.handleChanges}/>
                                    <div className="errorForm">
                                        {this.state.surnameError}
                                    </div>
                                </div>
                            </div>
                            <div className="email">
