import React, { Component } from 'react'

export default class Checkout extends Component {

    constructor(){
        super();
        this.state = {
            name: "",
            surname: "",
            address: "",
            city: "",
            state: "",
            email: ""
        }
    }

    handleChanges = event => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({
            [name] : value
        })
    }

    render() {
        return (
            <div className="form-wrapper">

            </div>
        )
    }
}
