import React, { Component } from 'react';

const ProductContext= React.createContext();

export default class ProductProvider extends Component {

    state={
        categories: [],
        products:[],
        sortedProducts: [],
        cart:[],
        cartTotal:0,
        onesToWatch: [],
        loading: true,
        type: "all",
        compatibility: "all",
        brand: "all",
        price: 0,
        minPrice: 0,
        maxPrice: 0,
    };

    setUp = async() => {
        try{
            let products=awaitthis.getProducts();
            let tempSorted=products.sort((a, b) => (a.price > b.price) ? 1 : -1);
            let onesToWatch=[tempSorted[0], tempSorted[1], tempSorted[2]];
            let minPrice=Math.min(...products.map(item => item.price));
            let maxPrice=Math.max(...products.map(item => item.price));
            let price=maxPrice;
            this.setState({
                products,
                sortedProducts:products,
                onesToWatch,
                maxPrice,
                minPrice,
                price
            })
        }catch(error){
            console.log(error);
        }//try_catch
    }//setUp

    //getProducts
    getProducts = async() => {
        let res = await fetch("http://localhost:9000/searchAPI?param=shoes");
        let products = await res.json();
        console.log(products);
        let tempProd=await products["hits"].map(item => {
            let id=item.productId;
            let name=item.productName;
            let price=item.price;
            let inCart=false;
            let qty=0;
            let total=0;
            let images=[item.image.link,item.image.link,item.image.link];
            let product={id,name,price,images,inCart,qty,total};
            return product;
        })
        return tempProd;
    }

    //getCart
    getCart = async() => {
        let cart = await fetch("http://localhost:9000/getBasketAPI")
        .then(res => res.json())
        .catch(err => err);
        let tempCart = [];
        if(cart["productItems"])
            cart["productitems"].map(product => {
                let id=product.productId;
                let price=product.price;
                let name=product.productName;
                let item={id,price,name};
                return item;
            });
        return tempCart;
    }//getCart

    //getCategories
    getCategories = async() => {
        let res = await fetch("http://localhost:9000/categoriesAPI");
        let categories = await res.json();
        console.log(categories);
        let tempCat = await categories["data"].map(item => {
            let id=item.id;
            let name=item.name.default;
            let category={id,name};
            return category;
        })
        return tempCat;
    }//getCategories

    //sort
    sort = event => {
        let {
            sortedProducts
        } = this.state;
        const value=event.target.value;
        if(value==="alpha")
            sortedProducts=sortedProducts.sort((a, b) => (a.name > b.name) ? 1 : -1);
        else if(value==="price")
            sortedProducts=sortedProducts.sort((a, b) => (a.price > b.price) ? 1 : -1);
        this.setState({sortedProducts:sortedProducts});
    }//sort

    //getProduct
    getProduct = (element) => {
        let tempProduct=[...this.state.products];
        const product=tempProduct.find(product => product.element === element);
        return product;
    }//getProduct

    //resetChanges
    resetChanges = event => {
        let{
            products
        } = this.state;
        this.setState({sortedProducts:products})
    }//resetChanges

    //handleChanges
    handleChanges = event => {
        const target=event.target;
        const value=target.type === 'checkbox' ? target.checked : target.value;
        const name=event.target.name;
        this.setState({[name]:value},this.filterProducts)
    }//handleChanges

    //addToCart
    addToCart = async(id) => {
        await fetch(`http://localhost:9000/addItemToBasketAPI?item=${id}`)
        .then(res => res.json())
        .catch(err => err);
        let cart=this.getCart();
        this.setState({cart});
    }

    //increment
    increment = async(id) => {

    }

    //decrement
    decrement = async(id) => {

    }

    //clearCart
    clearCart = () => {

    }

    //removeItem
    removeItem = async(id) => {

    }

    //restore
    restoreValues = () => {

    } 

    //addTotals
    addTotals = () => {

    }

    filterProducts = () => {
        let{
            products,
            type,
            compatibility,
            price,
            brand
        } = this.state;
        let tempProducts=[...products];

        //capacity
        if(compatibility!=='all')
            tempProducts=tempProducts.filter(product => product.compatibility.includes(compatibility))

        //type
        if(type!=='all')
            tempProducts=tempProducts.filter(product => product.type === type);

        //price
        price=parseInt(price);
        tempProducts=tempProducts.filter(product => product.price <= price);

        //brand
        if(brand!=='all')
                tempProducts=tempProducts.filter(product => product.brand === brand)

        this.setState({
            sortedProducts:tempProducts
        })
    }//filterProducts

    componentDidMount(){
        this.setUp();
    }

    render() {
        return (
            <ProductContext.Provider value={{...this.state,getProduct: this.getProduct}}>
                {this.props.children}
            </ProductContext.Provider>
        );
    }//render
}//ProductProvider

const ProductConsumer = ProductContext.Consumer;

export function withProductConsumer(Component){
    return function ConsumerWrapper(props){
        return(
            <ProductConsumer>
                {value => <Component {...props} context={value}/>}
            </ProductConsumer>
        )}
}//withProductConsumer

export{ProductProvider,ProductConsumer,ProductContext}
